INSERT INTO `zb_article_content` VALUES ('19', '25', '&lt;p&gt;基于无限级分类的两种方法代码如下&lt;br/&gt;&lt;/p&gt;&lt;p&gt;第一种方法&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;定义model类&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;public&amp;nbsp;function&amp;nbsp;getAdminCate($id=0,&amp;nbsp;$field=true)\r\n{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//获取所有分类信息\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$list&amp;nbsp;=&amp;nbsp;$this-&amp;gt;field($field)-&amp;gt;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//处理分类信息\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$list&amp;nbsp;=&amp;nbsp;&amp;nbsp;$this-&amp;gt;getHtmlCate($list,&amp;nbsp;&amp;#39;——&amp;#39;,&amp;nbsp;$id,&amp;nbsp;0);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//V($list);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//返回\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$list;\r\n}\r\nprivate&amp;nbsp;function&amp;nbsp;getHtmlCate($cate,$html=&amp;quot;——&amp;quot;,$pid=0,&amp;nbsp;$level=0)\r\n{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//定义空数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;array();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//遍历数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;foreach($cate&amp;nbsp;as&amp;nbsp;$v){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($v[&amp;#39;pid&amp;#39;]&amp;nbsp;==&amp;nbsp;$pid){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$v[&amp;#39;html&amp;#39;]&amp;nbsp;=&amp;nbsp;str_repeat($html,&amp;nbsp;$level);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$v[&amp;#39;level&amp;#39;]&amp;nbsp;=&amp;nbsp;$level;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr[]&amp;nbsp;=&amp;nbsp;$v;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$arr&amp;nbsp;=&amp;nbsp;array_merge($arr,$this-&amp;gt;getHtmlCate($cate,$html,$v[&amp;#39;id&amp;#39;],$level+1));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;return&amp;nbsp;$arr;\r\n}&lt;/pre&gt;&lt;p&gt;2.控制器调用model&lt;/p&gt;&lt;p&gt;$where = &amp;#39;&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;//实例化&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$model = D(&amp;#39;Category&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;//获取分类信息&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;if(!empty($_GET)){&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$name = urldecode(I(&amp;#39;get.name&amp;#39;));&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$where[&amp;#39;name&amp;#39;] = array(&amp;#39;like&amp;#39;,&amp;#39;%&amp;#39;.$name.&amp;#39;%&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$map[&amp;#39;name&amp;#39;] = $name;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$this-&amp;gt;assign(&amp;#39;where&amp;#39;,$name);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;}&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$count = $model-&amp;gt;where($where)-&amp;gt;count();&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page = new \\Think\\Page($count,5);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;lastSuffix = false;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;//定制分页样式&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;header&amp;#39;,&amp;#39;共有 %TOTAL_ROW% 个用户&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;prev&amp;#39;,&amp;#39;上一页&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;next&amp;#39;,&amp;#39;下一页&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;first&amp;#39;,&amp;#39;首页&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;last&amp;#39;,&amp;#39;尾页&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;//修改主题&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;setConfig(&amp;#39;theme&amp;#39;,&amp;#39; &amp;lt;ul class=&amp;quot;pagination&amp;quot;&amp;gt;&amp;lt;li&amp;gt;%FIRST%&amp;lt;/li&amp;gt; &amp;lt;li&amp;gt;%UP_PAGE%&amp;lt;/li&amp;gt; &amp;lt;li&amp;gt;%LINK_PAGE%&amp;lt;/li&amp;gt; &amp;lt;li&amp;gt;%DOWN_PAGE%&amp;lt;/li&amp;gt; &amp;lt;li&amp;gt;%END%&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;#39;);&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;foreach($map as $key=&amp;gt;$val) {&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$Page-&amp;gt;parameter[$key] = urlencode($val);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;}&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$show = $Page-&amp;gt;show();&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data = $model-&amp;gt;limit($Page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$Page-&amp;gt;listRows)-&amp;gt;where($where)-&amp;gt;getAdminCate();&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$this-&amp;gt;assign(&amp;#39;list&amp;#39;,$data);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$this-&amp;gt;assign(&amp;#39;page&amp;#39;,$show);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre;&quot;&gt;&lt;/span&gt;$this-&amp;gt;display(&amp;#39;Categorys/index&amp;#39;);&lt;/p&gt;&lt;p&gt;第二种方法&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;定义model类&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public function getHomeCate($id=0, $field=true){&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //获取所有分类信息&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $list = $this-&amp;gt;field($field)-&amp;gt;select();&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //处理分类信息&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $list = &amp;nbsp;$this-&amp;gt;getTreeCate($list, $id, &amp;#39;child&amp;#39;);&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //如果 存在id&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if (is_numeric($id) &amp;amp;&amp;amp; $id &amp;gt; 0) {&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $arr = $this-&amp;gt;field($field)-&amp;gt;find($id);&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $arr[&amp;#39;child&amp;#39;] = $list;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return $arr;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //V($list);&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //返回&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return $list;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;private function getTreeCate($cate,$pid=0,$name=&amp;quot;child&amp;quot;){&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $arr = array();&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //遍历&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; foreach($cate as $v){&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if($v[&amp;#39;pid&amp;#39;] == $pid){&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $v[$name] = $this-&amp;gt;getTreeCate($cate,$v[&amp;#39;id&amp;#39;]);&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $arr[] = $v;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return $arr;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');
INSERT INTO `zb_article_content` VALUES ('20', '27', '&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;网站的第三方登录QQ登录&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;第一步:&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size: 16px;&quot;&gt;认证QQ互联,申请appid和appkey&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; outline: 0px; vertical-align: baseline; font-size: 12px; line-height: 24px; word-wrap: break-word; word-break: break-all; color: rgb(93, 97, 97); font-family: &amp;quot;Microsoft YaHei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;appid&lt;/strong&gt;：应用的唯一标识。在OAuth2.0认证过程中，appid的值即为oauth_consumer_key的值。&lt;/p&gt;&lt;p style=&quot;padding: 0px; margin-top: 0px; margin-bottom: 0px; outline: 0px; vertical-align: baseline; font-size: 12px; line-height: 24px; word-wrap: break-word; word-break: break-all; color: rgb(93, 97, 97); font-family: &amp;quot;Microsoft YaHei&amp;quot;, arial; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;appkey&lt;/strong&gt;：appid对应的密钥，访问用户资源时用来验证应用的合法性。在OAuth2.0认证过程中，appkey的值即为oauth_consumer_secret的值。&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;/span&gt;第二步:&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;span style=&quot;font-size: 16px;&quot;&gt;放置QQ按钮,获取回调code&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;a&amp;nbsp;href=&amp;quot;https://graph.qq.com/oauth2.0/authorize?response_type=code&amp;amp;client_id=XXXXXXXXX&amp;amp;redirect_uri=http://blog.zhuxv.com&amp;amp;state=qqlogin&amp;amp;scope=get_user_info&amp;quot;&amp;gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;lt;img&amp;nbsp;src=&amp;quot;图片地址&amp;quot;&amp;gt;\r\n&amp;lt;/a&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 24px;&quot;&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;table class=&quot;t&quot; width=&quot;690&quot; style=&quot;width: 661px;&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;response_type&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red; font-size: 12px;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;授权类型，此值固定为“code”。&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;client_id&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red; font-size: 12px;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;申请QQ登录成功后，分配给应用的appid。&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;redirect_uri&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red; font-size: 12px;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;成功授权后的回调地址，必须是注册appid时填写的主域名下的地址，建议设置为网站首页或网站的用户中心。&lt;span style=&quot;font-size: 12px; color: red;&quot;&gt;注意需要将url进行URLEncode&lt;/span&gt;。&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;state&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red; font-size: 12px;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;client端的状态值。用于第三方应用防止CSRF攻击，成功授权后回调时会原样带回。请务必严格按照流程检查用户与state参数状态的绑定。&lt;/span&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;scope&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;可选&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;请求用户授权时向用户显示的可进行授权的列表。&lt;/span&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;可填写的值是&lt;/span&gt;&lt;a title=&quot;API列表&quot; href=&quot;http://wiki.connect.qq.com/api%e5%88%97%e8%a1%a8&quot; style=&quot;cursor: pointer; padding: 0px; margin: 0px; outline: 0px; vertical-align: baseline; text-decoration: underline; color: rgb(28, 128, 204); font-size: 12px;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;API文档&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;中列出的接口，以及一些动作型的授权（目前仅有：do_like），如果要填写多个接口名称，请用逗号隔开。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;例如：scope=get_user_info,list_album,upload_pic,do_like&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;不传则默认请求对接口get_user_info进行授权。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;color: red; font-size: 12px;&quot;&gt;建议控制授权项的数量，只传入必要的接口名称，因为授权项越多，用户越可能拒绝进行任何授权。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;display&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;可选&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;仅&lt;strong&gt;PC网站&lt;/strong&gt;接入时使用。&lt;/span&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;用于展示的样式。不传则默认展示为PC下的样式。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;如果传入“mobile”，则展示为mobile端下的样式。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;g_ut&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;可选&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;仅&lt;strong&gt;WAP网站&lt;/strong&gt;接入时使用。&lt;/span&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;QQ登录页面版本（1：wml版本； 2：xhtml版本），默认值为1。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;通过回调code获取access_token,首先定义一个curl方法开启会话&lt;br/&gt;&lt;/p&gt;&lt;p&gt;public function curl($url,$type,$data=&amp;#39;&amp;#39;)&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; {&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $c = curl_init();&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; curl_setopt($c, CURLOPT_URL, $url); //请求地址&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; curl_setopt($c, CURLOPT_RETURNTRANSFER, 1); //不输出数据&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //curl_setopt($c, CURLOPT_HTTPHEADER, array(&amp;#39;X-FORWARDED-FOR:200.98.182.163&amp;#39;, &amp;#39;CLIENT-IP:203.98.182.163&amp;#39;)); //构造IP&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //curl_setopt($ch, CURLOPT_REFERER, &amp;quot;http://www.baidu.com/ &amp;quot;); //构造来路&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //curl_setopt($c, CURLOPT_HEADER, 0);//如果你想把一个头包含在输出中，设置这个选项为一个非零值。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; curl_setopt($c, CURLOPT_POST, $type);//如果你想PHP去做一个正规的HTTP POST，设置这个选项为一个非零值。这个POST是普通的 application/x-www-from-urlencoded 类型，多数被HTML表单使用。&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if($type == 1){&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; curl_setopt($c, CURLOPT_POSTFIELDS, $data);//传递一个作为HTTP “POST”操作的所有数据的字符串。[&amp;#39;name&amp;#39;=&amp;gt;&amp;#39;张三&amp;#39;]也行&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $out = curl_exec($c); //执行 cURL 会话&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; curl_close($c); //关闭 cURL 会话&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; return $out;&lt;/p&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; }&lt;/p&gt;&lt;p&gt;定义一个获取access_token的方法&lt;/p&gt;&lt;p&gt;private function assess_token($code)&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;{&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$url = &amp;quot;https://graph.qq.com/oauth2.0/token&amp;quot;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;grant_type&amp;#39;] = &amp;#39;authorization_code&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;client_id&amp;#39;] = &amp;#39;XXXXXXXX&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;client_secret&amp;#39;] = &amp;#39;XXXXXXXXXXXXXXXXX&amp;#39;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;code&amp;#39;] = $code;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;redirect_uri&amp;#39;] = &amp;quot;http://blog.zhuxv.com/&amp;quot;;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$qq = $this-&amp;gt;curl($url,1,$data);&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;return $qq;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space: pre;&quot;&gt;&lt;/span&gt;}&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;table class=&quot;t&quot; width=&quot;690&quot; style=&quot;width: 661px;&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;grant_type&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;授权类型，在本步骤中，此值为“&lt;span style=&quot;color: rgb(255, 0, 0);&quot;&gt;authorization_code&lt;/span&gt;”。&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;client_id&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;申请QQ登录成功后，分配给网站的appid。&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;client_secret&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;申请QQ登录成功后，分配给网站的appkey。&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;code&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;上一步返回的authorization code。&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;如果用户成功登录并授权，则会跳转到指定的回调地址，并在URL中带上Authorization Code。&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;例如，回调地址为www.qq.com/my.php，则跳转到：&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;http://www.qq.com/my.php?code=520DD95263C1CFEA087******&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;注意此code会在10分钟内过期。&lt;/span&gt;&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;redirect_uri&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;与上面一步中传入的redirect_uri保持一致。&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;通过回调code获取access_token&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;$code&amp;nbsp;=&amp;nbsp;$request-&amp;gt;code;\r\n$access_token&amp;nbsp;=&amp;nbsp;$this-&amp;gt;access_token($code);\r\n$_SESSION[&amp;#39;access_token&amp;#39;]&amp;nbsp;=&amp;nbsp;$access_token;&amp;nbsp;//存入$_SESSION&lt;/pre&gt;&lt;p&gt;通过access_token获取用户openid,首先定义一个openid方法&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;private&amp;nbsp;function&amp;nbsp;getOpenId()\r\n	{\r\n		$url&amp;nbsp;=&amp;nbsp;&amp;#39;https://graph.qq.com/oauth2.0/me&amp;#39;;\r\n		$data[&amp;#39;access_token&amp;#39;]&amp;nbsp;=&amp;nbsp;$_SESSION[&amp;#39;access_token&amp;#39;][&amp;#39;access_token&amp;#39;];\r\n\r\n		$qqData&amp;nbsp;=&amp;nbsp;$this-&amp;gt;curl($url,1,$data);\r\n\r\n		return&amp;nbsp;$qqData;\r\n	}&lt;/pre&gt;&lt;p&gt;获取openid并转换为数组,存入$_SESSION&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;$qqOpenid&amp;nbsp;=&amp;nbsp;$this-&amp;gt;getOpenId();			\r\n$qqOpenidData&amp;nbsp;=&amp;nbsp;(array)json_decode(substr($qqOpenid,strpos($qqOpenid,&amp;#39;(&amp;#39;)+1,-3));\r\n$_SESSION[&amp;#39;qqlogin&amp;#39;][&amp;#39;openid&amp;#39;]&amp;nbsp;=&amp;nbsp;$qqOpenidData[&amp;#39;openid&amp;#39;];&lt;/pre&gt;&lt;table class=&quot;t&quot; width=&quot;690&quot; style=&quot;width: 661px;&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;access_token&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;&lt;span style=&quot;color: red;&quot;&gt;必须&lt;/span&gt;&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;在Step1中获取到的access token。&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;调用OPENAPI接口&lt;/p&gt;&lt;p&gt;&lt;strong style=&quot;color: rgb(93, 97, 97); font-family: &amp;quot;Microsoft YaHei&amp;quot;, arial; font-size: 12px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;调用所有OpenAPI时，除了各接口私有的参数外，所有OpenAPI都需要传入基于OAuth2.0协议的通用参数：&lt;/strong&gt;&lt;/p&gt;&lt;table class=&quot;t&quot; width=&quot;690&quot; style=&quot;width: 661px;&quot;&gt;&lt;tbody&gt;&lt;tr class=&quot;firstRow&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;access_token&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;可通过&lt;a title=&quot;使用Authorization_Code获取Access_Token&quot; href=&quot;http://wiki.connect.qq.com/%e4%bd%bf%e7%94%a8authorization_code%e8%8e%b7%e5%8f%96access_token&quot; style=&quot;cursor: pointer; padding: 0px; margin: 0px; outline: 0px; vertical-align: baseline; text-decoration: none; color: rgb(28, 128, 204);&quot;&gt;使用Authorization_Code获取Access_Token&lt;/a&gt;&amp;nbsp;或来获取。&amp;nbsp;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;access_token有3个月有效期。&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr class=&quot;h&quot; style=&quot;background-color: rgb(249, 249, 249);&quot;&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;oauth_consumer_key&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;申请QQ登录成功后，分配给应用的appid&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;openid&lt;/td&gt;&lt;td style=&quot;padding: 0px 0px 0px 20px; margin: 0px; outline: 0px; vertical-align: middle; border-right-color: rgb(219, 219, 219); border-bottom-color: rgb(219, 219, 219); line-height: 24px; word-wrap: break-word;&quot;&gt;用户的ID，与QQ号码一一对应。&amp;nbsp;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;outline: 0px; vertical-align: baseline; line-height: 24px; word-wrap: break-word; word-break: break-all;&quot;&gt;可通过调用https://graph.qq.com/oauth2.0/me?access_token=YOUR_ACCESS_TOKEN 来获取。&lt;/p&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/tbody&gt;&lt;/table&gt;&lt;p&gt;定义获取用户资料方法&lt;br/&gt;&lt;/p&gt;&lt;p&gt;private function getQ($openid)&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;{&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$url = &amp;quot;https://graph.qq.com/user/get_user_info&amp;quot;;&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;access_token&amp;#39;] = $_SESSION[&amp;#39;qqlogin&amp;#39;][&amp;#39;access_token&amp;#39;];&lt;/p&gt;&lt;p&gt;&lt;span class=&quot;Apple-tab-span&quot; style=&quot;white-space:pre&quot;&gt;&lt;/span&gt;$data[&amp;#39;oauth_consumer_key&amp;#39;] = &amp